import React from 'react';
import { motion } from 'framer-motion';

const doctors = [
  { name: 'Dr. John Doe', specialty: 'Cardiologist', image: '/placeholder.svg?height=200&width=200' },
  { name: 'Dr. Jane Smith', specialty: 'Neurologist', image: '/placeholder.svg?height=200&width=200' },
  { name: 'Dr. Mike Johnson', specialty: 'Orthopedic Surgeon', image: '/placeholder.svg?height=200&width=200' },
  { name: 'Dr. Sarah Brown', specialty: 'Pediatrician', image: '/placeholder.svg?height=200&width=200' },
  { name: 'Dr. Chris Lee', specialty: 'Dermatologist', image: '/placeholder.svg?height=200&width=200' },
];

const DoctorCards: React.FC = () => {
  return (
    <section id="doctors" className="my-12">
      <h2 className="text-3xl font-bold text-center mb-8">Our Doctors</h2>
      <div className="overflow-x-auto">
        <div className="flex space-x-6 pb-4">
          {doctors.map((doctor, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.05 }}
              className="bg-white rounded-lg shadow-md p-6 flex-shrink-0 w-64"
            >
              <img src={doctor.image} alt={doctor.name} className="w-full h-48 object-cover rounded-lg mb-4" />
              <h3 className="text-xl font-semibold mb-2">{doctor.name}</h3>
              <p className="text-gray-600">{doctor.specialty}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default DoctorCards;
